import { Box, Input, FormLabel, FormErrorMessage } from "@chakra-ui/react";

export const InputDiv = ({ name, register, errors = "" }) => {
  return (
    <>
      <Box w="100%" mt="20px">
        <FormLabel color="#F8F9FA" fontSize="xs" htmlFor={name}>
          {name}
        </FormLabel>
      </Box>
      <Input
        placeholder={`Digite seu ${name.toLowerCase()}`}
        bg="#343B41"
        color="#F8F9FA"
        {...register(name.toLowerCase())}
      />
      <FormErrorMessage>{errors[name]?.message}</FormErrorMessage>
    </>
  );
};
