import {
  Button,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
} from "@chakra-ui/react";

import { InputDiv } from "../InputDiv";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

export function Addtechnology({ children }) {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const schema = yup.object().shape({
    title: yup.string().required("Campo obrigatório"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmitFunction = (data) => {
    console.log(data);
    onClose();
  };

  return (
    <>
      <Button
        onClick={onOpen}
        w="32px"
        h="32px"
        bg="#212529"
        color="#FFFFFF"
        fontSize="xl"
      >
        {children}
      </Button>

      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <form onSubmit={handleSubmit(onSubmitFunction)}>
          <ModalContent mt="130px" w="270px" borderRadius="3px">
            <ModalHeader color="#F8F9FA" fontSize="xs" bgColor="#343B41">
              Cadastrar tecnologia
            </ModalHeader>
            <ModalCloseButton color="#868E96" fontSize="10px" />
            <ModalBody bgColor="#212529">
              <InputDiv
                label="Nome"
                name="title"
                register={register}
                error={errors.title?.message}
                placeholder="Digite a tecnologia"
              />
            </ModalBody>

            <ModalFooter>
              <Button type="submit">Adicionar</Button>
            </ModalFooter>
          </ModalContent>
        </form>
      </Modal>
    </>
  );
}
