import { Box, Text, VStack, Flex, Button, Heading } from "@chakra-ui/react";

import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
//import { addPointerEvent } from "framer-motion";

import api from "../../services/api";

import { InputDiv } from "../../components/InputDiv";
import { useHistory } from "react-router-dom";

export const Signin = () => {
  const schema = yup.object().shape({
    email: yup.string().required("Campo obrigatório").email("Email inválido"),
    password: yup.string().required("Campo obrigatório"),
  });

  const history = useHistory();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmitFunction = (data) => {
    console.log(data); // retirar
    api.post("/sessions", data).then((res) => {
      console.log(res.data); //gabrielNOVO@kenzie.com.br, 123456789
      //add toast
      const { token, user } = res.data;
      localStorage.setItem("@KenzieHub:token", JSON.stringify(token));
      localStorage.setItem("@KenzieHub:user", JSON.stringify(user));
    });
  };

  return (
    <Flex
      width="100vw"
      height="100vh"
      bgColor="#121214"
      direction="column"
      justify="flex-start"
      align="center"
    >
      <Flex m="30px 0px 10px 0px " justify="space-around" width="340px">
        <Heading color="#FF577F" fontSize="xl">
          Kenzie Hub
        </Heading>
        <Button
          w="80px"
          bgColor="#212529"
          height="26px"
          color="#FFF"
          fontSize="10px"
          onClick={() => history.push("/")}
        >
          Voltar
        </Button>
      </Flex>
      <VStack
        w="280px"
        h="450px"
        bgColor="#212529"
        p="15px 20px"
        borderRadius="4px"
        mt="10px"
      >
        <Heading color="#FFF" fontSize="lg">
          Crie sua conta
        </Heading>
        <Text color="#868E96" fontSize="10px">
          Rápido e grátis. Vamos nessa!
        </Text>
        <form onSubmit={handleSubmit(onSubmitFunction)}>
          <InputDiv
            label="Email"
            name="email"
            register={register}
            error={errors.email?.message}
            placeholder="Digite seu email"
          />
          <InputDiv
            label="Senha"
            name="password"
            register={register}
            error={errors.password?.message}
            placeholder="Digite sua senha"
          />

          <Button
            w="100%"
            mt="20px"
            bgColor="#FF577F"
            color="#FFF"
            type="submit"
            borderRadius="4px"
            fontWeight="light"
          >
            Entrar
          </Button>
        </form>
        <Box pt="30px">
          <Text color="#868E96" fontSize="xs">
            Ainda não possui uma conta?
          </Text>
        </Box>
        <Button
          isFullWidth
          bgColor="#868E96"
          color="#FFF"
          borderRadius="4px"
          fontWeight="light"
        >
          Cadastre-se
        </Button>
      </VStack>
    </Flex>
  );
};
