import {
  Box,
  Input,
  FormLabel,
  FormErrorMessage,
  FormControl,
} from "@chakra-ui/react";

export const InputDiv = ({ name, register, error = "", ...rest }) => {
  return (
    <FormControl isInvalid={error}>
      <Box w="100%" mt="20px">
        <FormLabel color="#F8F9FA" fontSize="xs" htmlFor={name}>
          {name}
        </FormLabel>
      </Box>
      <Input bg="#343B41" color="#F8F9FA" {...register(name.toLowerCase())} />
      <FormErrorMessage>{error}</FormErrorMessage>
    </FormControl>
  );
};
