import { Redirect } from "react-router-dom";
import { useState } from "react";
import { Box, Button, Container, Flex, Heading, Text } from "@chakra-ui/react";
import { useHistory } from "react-router-dom";

import { useEffect } from "react";

import { Addtechnology } from "../../components/Modal/Addtechnology";
import api from "../../services/api";

export const Dashboard = ({ authenticated }) => {
  const [token] = useState(
    JSON.parse(localStorage.getItem("@KenzieHub:token")) || ""
  );
  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("@KenzieHub:user")) || ""
  );

  const history = useHistory();

  const loadTechs = () => {
    api.get();
  };

  useEffect(() => {
    localStorage.removeItem("@KenzieHub:user");
    localStorage.setItem("@KenzieHub:user", JSON.stringify(user));
  }, [user]);

  if (!authenticated) return <Redirect to="/" />;

  console.log(user);
  console.log(token);

  return (
    <Container w="100vw" h="100vh" bg="#121214" p="0px">
      <Flex p="15px 0px 20px 0px " justify="space-around" minWidth="280px">
        <Heading color="#FF577F" fontSize="xl">
          Kenzie Hub
        </Heading>
        <Button
          w="100px"
          bgColor="#212529"
          height="26px"
          color="#FFF"
          fontSize="12px"
          onClick={() => {
            localStorage.clear();
            history.push("/login");
          }}
        >
          Sair
        </Button>
      </Flex>
      <Box
        p="30px 0px 30px 10px"
        borderTop="1px solid #36363b"
        borderBottom="1px solid #36363b"
        w="100vw"
      >
        <Heading color="#F8F9FA" fontSize="md" pl="20px">
          Olá, {user.name}
        </Heading>
        <Text color="#868E96" mt="8px" pl="20px">
          {user.course_module}
        </Text>
      </Box>
      <Container mt="30px">
        <Flex justify="space-between" pr="10px">
          <Heading color="#F8F9FA" fontSize="lg">
            Tecnologias
          </Heading>
          <Addtechnology token={token} user={user} setUser={setUser}>
            +
          </Addtechnology>
        </Flex>
        <Flex
          w="280px"
          bgColor="#212529"
          borderRadius="4px"
          mt="20px"
          direction="column"
          p="10px 0px"
        >
          <Flex
            minWidth="calc(100% - 20px)"
            h="50px"
            m="10px 10px"
            bg="#121214"
            borderRadius="4px"
            justify="space-between"
            align="center"
          >
            <Heading color="#F8F9FA" fontSize="md" pl="10px">
              React JS
            </Heading>
            <Text color="#868E96" fontSize="md" pr="10px">
              Intermediário
            </Text>
          </Flex>
        </Flex>
      </Container>
    </Container>
  );
};
