import { Redirect } from "react-router-dom";
import { useState } from "react";
import { Box, Button, Container, Flex, Heading } from "@chakra-ui/react";
import { useHistory } from "react-router-dom";

export const Dashboard = ({ authenticated }) => {
  const [token] = useState(
    JSON.parse(localStorage.getItem("@KenzieHub:token")) || ""
  );
  const [user] = useState(
    JSON.parse(localStorage.getItem("@KenzieHub:user")) || ""
  );

  const history = useHistory();

  if (!authenticated) return <Redirect to="/" />;

  console.log(user);
  console.log(token);

  return (
    <Container w="100vw" h="100vh" bg="#121214">
      <Flex p="15px 0px 20px 0px " justify="space-around" minWidth="280px">
        <Heading color="#FF577F" fontSize="xl">
          Kenzie Hub
        </Heading>
        <Button
          w="80px"
          bgColor="#212529"
          height="26px"
          color="#FFF"
          fontSize="10px"
          onClick={() => {
            localStorage.clear();
            history.push("/login");
          }}
        >
          Sair
        </Button>
      </Flex>
      <Box mt="10px">
        <Heading color="#F8F9FA" fontSize="md">
          Ol√°, {user.name}
        </Heading>
      </Box>
      <text color="#868E96">{user.name}</text>
    </Container>
  );
};
